import styled from 'styled-components';

export const Container  =  styled.div`
    display: grid;
    //3 colunas de tamanhos iguais
    grid-template-columns: repeat(3,1fr);
    gap: 2rem;
    margin-top:-10rem;


    div{
        background: var(--shape);
        padding: 1.5rem 2rem;
        border: ra;

    }

`;
